// Ê™îÊ°à: src/components/FortuneTeller.jsx
import React, { useState, useCallback, useEffect } from 'react';
import './FortuneTeller.css';
import { Converter } from 'opencc-js';

// ÂòóË©¶Âª∫Á´ã converterÔºàopencc-js ‰∏çÂêåÁâàÊú¨ÂõûÂÇ≥ÂûãÊÖãÂèØËÉΩ‰∏çÂêåÔºâ
let rawConverter = null;
try {
  rawConverter = Converter ? Converter({ from: 'cn', to: 'tw' }) : null;
} catch (e) {
  // fallback: Ëã•ÂàùÂßãÂåñÂ§±ÊïóÔºårawConverter Áïô null
  rawConverter = null;
}

// ÂÆâÂÖ®ÁöÑËΩâÊèõÂáΩÂºèÔºöËã• converter ÊòØ function -> ÂëºÂè´ÔºõËã•ÊòØÁâ©‰ª∂‰∏îÊúâ .convert -> ÂëºÂè´ÔºõÂê¶ÂâáÂõûÂÇ≥ÂéüÊñá
const safeConvert = (text) => {
  if (!text || typeof text !== 'string') return text || '';
  try {
    if (!rawConverter) return text;
    if (typeof rawConverter === 'function') return rawConverter(text);
    if (rawConverter && typeof rawConverter.convert === 'function') return rawConverter.convert(text);
    return text;
  } catch (e) {
    console.warn('ËΩâÊèõÂ§±ÊïóÔºåÂõûÂÇ≥ÂéüÊñá', e);
    return text;
  }
};

// ÊòüÂ∫ßÈÅ∏È†Ö
const ZODIAC_OPTIONS = [
  { value: "aries", label: "‚ôà Áâ°ÁæäÂ∫ß" },
  { value: "taurus", label: "‚ôâ ÈáëÁâõÂ∫ß" },
  { value: "gemini", label: "‚ôä ÈõôÂ≠êÂ∫ß" },
  { value: "cancer", label: "‚ôã Â∑®ËüπÂ∫ß" },
  { value: "leo", label: "‚ôå ÁçÖÂ≠êÂ∫ß" },
  { value: "virgo", label: "‚ôç ËôïÂ•≥Â∫ß" },
  { value: "libra", label: "‚ôé Â§©Áß§Â∫ß" },
  { value: "scorpio", label: "‚ôè Â§©Ë†çÂ∫ß" },
  { value: "sagittarius", label: "‚ôê Â∞ÑÊâãÂ∫ß" },
  { value: "capricorn", label: "‚ôë Êë©ÁæØÂ∫ß" },
  { value: "aquarius", label: "‚ôí Ê∞¥Áì∂Â∫ß" },
  { value: "pisces", label: "‚ôì ÈõôÈ≠öÂ∫ß" },
];

const TIME_OPTIONS = [
  { value: "today", label: "‰ªäÊó•" },
  { value: "week", label: "Êú¨ÈÄ±" },
  { value: "month", label: "Êú¨Êúà" },
];

const FortuneTeller = () => {
  const [activeTab, setActiveTab] = useState('constellation');

  // fortune
  const [selectedZodiac, setSelectedZodiac] = useState(ZODIAC_OPTIONS[0].value);
  const [selectedTime, setSelectedTime] = useState(TIME_OPTIONS[0].value);
  const [fortuneResult, setFortuneResult] = useState({
    title: 'Ë´ãÈÅ∏ÊìáÊÇ®ÁöÑÊòüÂ∫ß‰∏¶ÈªûÊìä„ÄåÊü•ÁúãÈÅãÂã¢„Äç„ÄÇ',
    content: ''
  });
  const [isLoading, setIsLoading] = useState(false);

  // tarot
  const [tarotCardRevealed, setTarotCardRevealed] = useState(false);
  const [tarotResult, setTarotResult] = useState({
    title: 'Â°îÁæÖÁâåÁµêÊûúÂ∞áÂú®Ê≠§ËôïÊè≠Á§∫„ÄÇ',
    meaning: '',
    cardName: '',
    imageUrl: ''
  });

  // traits
  const [traitsData, setTraitsData] = useState({});
  const [isTraitsLoading, setIsTraitsLoading] = useState(false);

  // ÊòüÂ∫ßÈÅãÂã¢ÔºöXXAPI
  const handleShowFortune = useCallback(async () => {
    setIsLoading(true);
    setFortuneResult({ title: 'üîÆ Ê≠£Âú®ËÆÄÂèñÈÅãÂã¢...', content: '' });

    const apiUrl = `https://v2.xxapi.cn/api/horoscope?type=${selectedZodiac}&time=${selectedTime}`;

    try {
      const res = await fetch(apiUrl);
      if (!res.ok) throw new Error(`API ÈåØË™§ ${res.status}`);
      const data = await res.json();

      // Áõ°ÈáèÂèñÂà∞ÊñáÂ≠óÔºà‰∏çÂêåÁâàÊú¨ÂèØËÉΩ‰ΩçÁΩÆ‰∏çÂêåÔºâ
      let fortuneText =
        data?.data?.fortunetext?.all ||
        data?.data?.all ||
        data?.fortunetext ||
        data?.all ||
        JSON.stringify(data);

      // ÁßªÈô§„ÄåÊòü6Â∫ß6Â±ã„ÄçÈ°ûÈõúË®ä
      fortuneText = (fortuneText || '').replace(/Êòü\d+Â∫ß\d+Â±ã[^\u4e00-\u9fa5]*/g, '').trim();

      // Á∞°È´îËΩâÁπÅÈ´îÔºàËã•ÁÑ° converter ÊúÉÂõûÂÇ≥ÂéüÊñáÔºâ
      fortuneText = safeConvert(fortuneText);

      setFortuneResult({
        title: `${ZODIAC_OPTIONS.find(z => z.value === selectedZodiac)?.label} ${TIME_OPTIONS.find(t => t.value === selectedTime)?.label}ÈÅãÂã¢Ôºö`,
        content: fortuneText
      });
    } catch (e) {
      console.error('ËÆÄÂèñÈÅãÂã¢Â§±Êïó', e);
      setFortuneResult({ title: '‚ùå ÈÅãÂã¢ËÆÄÂèñÂ§±Êïó', content: 'Ë´ãÁ®çÂæåÂÜçË©¶ÊàñÊ™¢Êü•Á∂≤Ë∑Ø / API„ÄÇ' });
    } finally {
      setIsLoading(false);
    }
  }, [selectedZodiac, selectedTime]);

  // Â°îÁæÖÊäΩÁâåÔºàcard ÊàñÊåâÈàïÁöÜÂèØËß∏ÁôºÔºâ
  const handleDrawTarot = useCallback(async () => {
    if (tarotCardRevealed) {
      // ÈáçÊñ∞Ë®≠ÂÆöÂõûÂàùÂßã
      setTarotCardRevealed(false);
      setTarotResult({ title: 'Â°îÁæÖÁâåÁµêÊûúÂ∞áÂú®Ê≠§ËôïÊè≠Á§∫„ÄÇ', meaning: '', cardName: '', imageUrl: '' });
      return;
    }

    try {
      const res = await fetch('https://tarotapi.dev/api/v1/cards/random?n=1');
      if (!res.ok) throw new Error('Tarot API ÈåØË™§');
      const data = await res.json();
      const card = data?.cards?.[0];
      if (!card) throw new Error('ÁâåË≥áÊñôÊ†ºÂºèÈåØË™§');

      setTarotCardRevealed(true);
      setTarotResult({
        title: `üîÆ ÊäΩÂà∞Ôºö${card.name}`,
        meaning: card.meaning_up || card.meaning_rev || '',
        cardName: card.name || '',
        imageUrl: card.image || ''
      });
    } catch (e) {
      console.error('ÊäΩÂ°îÁæÖÂ§±Êïó', e);
      setTarotResult({ title: '‚ùå ÊäΩÁâåÂ§±Êïó', meaning: 'Ë´ãÁ®çÂæåÂÜçË©¶„ÄÇ', cardName: '', imageUrl: '' });
    }
  }, [tarotCardRevealed]);

  // ÊòüÂ∫ßÁâπË≥™ÔºàRoxyAPIÔºâ - ÂÆπÈåØÂºèËß£Êûê
  const fetchAllZodiacTraits = useCallback(async () => {
    setIsTraitsLoading(true);
    const apiKey = "8e66fa8b-7990-47ac-8f03-944cef9a180d";
    const out = {};

    for (const zodiac of ZODIAC_OPTIONS) {
      try {
        const url = `https://roxyapi.com/api/v1/data/astro/astrology/zodiac/${zodiac.value}?token=${apiKey}`;
        const res = await fetch(url);
        if (!res.ok) throw new Error(`RoxyAPI ${res.status}`);
        const data = await res.json();

        // ÂòóË©¶Â§öÁ®ÆÂèØËÉΩÁöÑÊ¨Ñ‰Ωç‰ΩçÁΩÆ
        const payload = data?.data || data;
        const traits = payload?.traits || payload;

        // ÂòóË©¶Â§öÂÄã key ‰ª•ÂèñÂæó element/strengths/weaknesses/description Á≠â
        const element =
          traits?.element ||
          traits?.type ||
          payload?.element ||
          payload?.type ||
          '';

        const strengths =
          traits?.strengths ||
          traits?.advantages ||
          traits?.positive ||
          traits?.strength ||
          traits?.good ||
          payload?.strengths ||
          '';

        const weaknesses =
          traits?.weaknesses ||
          traits?.disadvantages ||
          traits?.negative ||
          traits?.weakness ||
          traits?.bad ||
          payload?.weaknesses ||
          '';

        out[zodiac.value] = {
          element: safeConvert(String(element || '')),
          strengths: safeConvert(String(strengths || '')),
          weaknesses: safeConvert(String(weaknesses || ''))
        };
      } catch (e) {
        console.warn('ÂèñÂæóÊòüÂ∫ßÁâπË≥™Â§±Êïó', zodiac.value, e);
        out[zodiac.value] = null;
      }
    }

    setTraitsData(out);
    setIsTraitsLoading(false);
  }, []);

  // È†ÅÈù¢ËºâÂÖ•Â∞±ÊäìÊòüÂ∫ßÁâπË≥™ÔºàÈùûÈªû tab ÊôÇÊäìÔºâ
  useEffect(() => {
    fetchAllZodiacTraits();
  }, [fetchAllZodiacTraits]);

  // UI - ÂêÑÂçÄÂ°ä
  const renderConstellationSection = () => (
    <section className="content-section active">
      <h2>‰ªäÊó•ÊòüÂ∫ßÈÅãÂã¢ ‚ú®</h2>

      <div className="zodiac-selector">
        <select value={selectedZodiac} onChange={(e) => setSelectedZodiac(e.target.value)}>
          {ZODIAC_OPTIONS.map(z => <option key={z.value} value={z.value}>{z.label}</option>)}
        </select>

        <select value={selectedTime} onChange={(e) => setSelectedTime(e.target.value)}>
          {TIME_OPTIONS.map(t => <option key={t.value} value={t.value}>{t.label}</option>)}
        </select>

        <button id="show-fortune-btn" onClick={handleShowFortune} disabled={isLoading}>
          {isLoading ? 'ÈÅãÁÆó‰∏≠...' : 'Êü•ÁúãÈÅãÂã¢'}
        </button>
      </div>

      <div className="result-box">
        <h3>{fortuneResult.title}</h3>
        <p>{fortuneResult.content}</p>
      </div>
    </section>
  );

  const renderTarotSection = () => (
    <section className="content-section">
      <h2>üîÆ Á∑ö‰∏äÂ°îÁæÖÊäΩÁâå</h2>

      <div className="tarot-area">
        <div id="tarot-card" className={tarotCardRevealed ? 'card-reveal' : ''} onClick={handleDrawTarot}>
          <div className="card-back">ÈªûÊìäÊäΩÁâå</div>
          <div className="card-front">
            {tarotResult.imageUrl ? <img src={tarotResult.imageUrl} alt={tarotResult.cardName} /> : 'ÈªûÊìäÊäΩÁâå'}
          </div>
        </div>
      </div>

      <button id="draw-tarot-btn" onClick={handleDrawTarot}>
        {tarotCardRevealed ? 'ÈáçÊñ∞ÊäΩÁâå' : 'ÊäΩÂèñ‰ªäÊó•Â°îÁæÖÁâå'}
      </button>

      <div className="result-box">
        <h3>{tarotResult.title}</h3>
        <p>{tarotResult.meaning}</p>
      </div>
    </section>
  );

  const renderInfoSection = () => (
    <section className="content-section">
      <h2>üìñ ÂçÅ‰∫åÊòüÂ∫ßÁâπË≥™</h2>

      {isTraitsLoading && <p>ËºâÂÖ•‰∏≠...</p>}

      <div className="constellation-grid">
        {ZODIAC_OPTIONS.map(z => {
          const info = traitsData[z.value];
          return (
            <div className="constellation-card" key={z.value}>
              <h3>{z.label}</h3>
              {!info && <p>ËºâÂÖ•‰∏≠ÊàñÂ∞öÁÑ°Ë≥áÊñô</p>}
              {info && (
                <>
                  <p><strong>‚ú® ÂÖÉÁ¥†Ôºö</strong> {info.element || '‚Äî'}</p>
                  <p><strong>üí™ ÂÑ™ÈªûÔºö</strong> {info.strengths || '‚Äî'}</p>
                  <p><strong>‚ö†Ô∏è Áº∫ÈªûÔºö</strong> {info.weaknesses || '‚Äî'}</p>
                </>
              )}
            </div>
          );
        })}
      </div>
    </section>
  );

  return (
    <>
      <header>
        <h1>üåü ÂëΩÈÅã‰πãËº™ üåü</h1>
        <nav>
          <button className={`nav-btn ${activeTab === 'constellation' ? 'active' : ''}`} onClick={() => setActiveTab('constellation')}>ÊòüÂ∫ßÈÅãÂã¢</button>
          <button className={`nav-btn ${activeTab === 'tarot' ? 'active' : ''}`} onClick={() => setActiveTab('tarot')}>Â°îÁæÖÂç†Âçú</button>
          <button className={`nav-btn ${activeTab === 'info' ? 'active' : ''}`} onClick={() => setActiveTab('info')}>ÊòüÂ∫ß‰ªãÁ¥π</button>
        </nav>
      </header>

      <main>
        {activeTab === 'constellation' && renderConstellationSection()}
        {activeTab === 'tarot' && renderTarotSection()}
        {activeTab === 'info' && renderInfoSection()}
      </main>

      <footer>
        <p>&copy; 2025 ÂëΩÈÅã‰πãËº™È≠îÊ≥ïÂ±ã„ÄÇÂÉÖ‰æõÂ®õÊ®ÇÂèÉËÄÉ„ÄÇ</p>
      </footer>
    </>
  );
};

export default FortuneTeller;
