// src/components/FortuneTeller.jsx (Êï¥Âêà LibreTranslate ÁøªË≠Ø„ÄÅRoxy„ÄÅNASA APOD ËàáÈåØË™§ËôïÁêÜ)
import React, { useState, useCallback, useEffect } from 'react';
import './FortuneTeller.css';
import { Converter } from 'opencc-js';

// Á∞°È´îËΩâÁπÅÈ´î (Áî®ÊñºÁ¢∫‰øùÊâÄÊúâ‰∏≠ÊñáÈÉΩÊòØÁπÅÈ´î)
const converter = Converter({ from: 'cn', to: 'tw' });

// ‚≠ê Roxy API Token (‰Ω†‰πãÂæåË¶ÅÊèõÊñ∞ÁöÑ token)
const API_TOKEN = '05239ac6-1087-4576-9fa9-d83860a4e722';

// ‚≠ê NASA API KeyÔºàË´ãÊèõÊàê‰Ω†ÁöÑ keyÔºâ
const NASA_API_KEY = 't9Z5pJEV980lItvNoFknpSKBeqtLMnaPAP7akzg4';

// ----------------------------------------------------
// üåü LibreTranslate ÁøªË≠ØËºîÂä©ÂáΩÂºè (Ëã± -> ‰∏≠ -> ÁπÅÈ´î)
// ----------------------------------------------------
const translateToChinese = async (text) => {
  if (!text || typeof text !== 'string' || text.trim() === '') return text;

  try {
    const response = await fetch('https://libretranslate.com/translate', {
      method: 'POST',
      body: JSON.stringify({
        q: text,
        source: "en",
        target: "zh", // ‰∏≠Êñá
        format: "text",
      }),
      headers: { "Content-Type": "application/json" }
    });

    if (!response.ok) {
      throw new Error(`LibreTranslate API Error: ${response.status}`);
    }

    const data = await response.json();
    const translatedText = data.translatedText;

    // ‰ΩøÁî® OpenCC Â∞áÁøªË≠ØÂæåÁöÑÁ∞°È´î‰∏≠ÊñáËΩâÊèõÁÇ∫ÁπÅÈ´î‰∏≠Êñá
    return converter(translatedText);

  } catch (error) {
    console.warn("ÁøªË≠ØË´ãÊ±ÇÂ§±ÊïóÊàñË∂ÖÊôÇÔºåËøîÂõûÂéüÊñá„ÄÇ", error);
    // ÁøªË≠ØÂ§±ÊïóÊôÇÔºåËøîÂõûÂéüÂßãÊñáÊú¨
    return text;
  }
};
// ----------------------------------------------------


// ÊòüÂ∫ßÈÅ∏È†Ö
const ZODIAC_OPTIONS = [
  { value: "aries", label: "‚ôà Áâ°ÁæäÂ∫ß" },
  { value: "taurus", label: "‚ôâ ÈáëÁâõÂ∫ß" },
  { value: "gemini", label: "‚ôä ÈõôÂ≠êÂ∫ß" },
  { value: "cancer", label: "‚ôã Â∑®ËüπÂ∫ß" },
  { value: "leo", label: "‚ôå ÁçÖÂ≠êÂ∫ß" },
  { value: "virgo", label: "‚ôç ËôïÂ•≥Â∫ß" },
  { value: "libra", label: "‚ôé Â§©Áß§Â∫ß" },
  { value: "scorpio", label: "‚ôè Â§©Ë†çÂ∫ß" },
  { value: "sagittarius", label: "‚ôê Â∞ÑÊâãÂ∫ß" },
  { value: "capricorn", label: "‚ôë Êë©ÁæØÂ∫ß" },
  { value: "aquarius", label: "‚ôí Ê∞¥Áì∂Â∫ß" },
  { value: "pisces", label: "‚ôì ÈõôÈ≠öÂ∫ß" },
];

const TIME_OPTIONS = [
  { value: "today", label: "‰ªäÊó•" },
  { value: "week", label: "Êú¨ÈÄ±" },
  { value: "month", label: "Êú¨Êúà" },
];

const FortuneTeller = () => {
  const [activeTab, setActiveTab] = useState('constellation');

  // ‚≠ê ÊòüÂ∫ßÈÅãÂã¢
  const [selectedZodiac, setSelectedZodiac] = useState(ZODIAC_OPTIONS[0].value);
  const [selectedTime, setSelectedTime] = useState(TIME_OPTIONS[0].value);
  const [fortuneResult, setFortuneResult] = useState({
    title: 'Ë´ãÈÅ∏ÊìáÊÇ®ÁöÑÊòüÂ∫ß‰∏¶ÈªûÊìä„ÄåÊü•ÁúãÈÅãÂã¢„Äç‰ª•ÈñãÂßã„ÄÇ',
    content: ''
  });
  const [isLoading, setIsLoading] = useState(false);

  // ‚≠ê ÊòüÂ∫ßÁâπË≥™
  const [traitsData, setTraitsData] = useState({});
  const [isTraitsLoading, setIsTraitsLoading] = useState(false);

  // ‚≠ê ‰∏âÂºµÂ°îÁæÖÁâå
  const [tarotCards, setTarotCards] = useState([]);
  const [isTarotLoading, setIsTarotLoading] = useState(false);

  // ‚≠ê NASA APOD
  const [nasaData, setNasaData] = useState(null);
  const [isNasaLoading, setIsNasaLoading] = useState(false);
  const [nasaError, setNasaError] = useState(null);

  // ---------------------- ÊòüÂ∫ßÈÅãÂã¢ ----------------------
  const handleShowFortune = useCallback(async () => {
    setIsLoading(true);
    setFortuneResult({ title: 'üîÆ Ê≠£Âú®ËÅØÁπ´ÂëΩÈÅã‰πãËº™...', content: '' });

    const apiUrl = `https://v2.xxapi.cn/api/horoscope?type=${selectedZodiac}&time=${selectedTime}`;
    try {
      const response = await fetch(apiUrl);
      if (!response.ok) throw new Error(`API ÈåØË™§: ${response.status}`);

      const data = await response.json();
      let fortuneText = data.data?.fortunetext?.all || 'ÈÅãÂã¢Êö´ÁÑ°Ë≥áÊñô„ÄÇ';
      // ÁßªÈô§Âª£ÂëäÂíå‰∏çÁõ∏ÈóúÊñáÂ≠ó
      fortuneText = fortuneText.replace(/Êòü\d+Â∫ß\d+Â±ã[^\u4e00-\u9fa5]*/g, '').trim();
      // Á¢∫‰øùÊòØÁπÅÈ´î
      fortuneText = converter(fortuneText);

      setFortuneResult({
        title: `${ZODIAC_OPTIONS.find(z => z.value === selectedZodiac)?.label} ${TIME_OPTIONS.find(t => t.value === selectedTime)?.label}ÈÅãÂã¢Ôºö`,
        content: fortuneText
      });
    } catch (apiError) {
      console.error('ÊòüÂ∫ßÈÅãÂã¢ API ËÆÄÂèñÂ§±Êïó:', apiError);
      setFortuneResult({ title: '‚ùå ÈÅãÂã¢ËÆÄÂèñÂ§±Êïó', content: 'Ë´ãÁ®çÂæåÂÜçË©¶„ÄÇ' });
    }
    setIsLoading(false);
  }, [selectedZodiac, selectedTime]);

  // ---------------------- ÊòüÂ∫ßÁâπË≥™ (Êñ∞Â¢ûÁøªË≠ØËàáÈåØË™§ËôïÁêÜ) ----------------------
  const fetchAllZodiacTraits = useCallback(async () => {
    setIsTraitsLoading(true);
    const result = {};

    for (const zodiac of ZODIAC_OPTIONS) {
      try {
        const url = `https://roxyapi.com/api/v1/data/astro/astrology/zodiac/${zodiac.value}?token=${API_TOKEN}`;
        const res = await fetch(url);
        if (!res.ok) throw new Error(`Roxy API ÈåØË™§: ${res.status}`);
        const data = await res.json();

        // Á¢∫‰øùËá≥Â∞ëÊúâÂéüÂßãË≥áÊñô
        let rawElement = data.element || "";
        let rawPersonality = data.personality || "";

        // ÂòóË©¶ÁøªË≠Ø„ÄÇÂ¶ÇÊûúÁøªË≠ØÂ§±ÊïóÔºåtranslateToChinese ÊúÉËøîÂõûÂéüÂßãÊñáÊú¨ (rawElement/rawPersonality)
        const translatedElement = await translateToChinese(rawElement);
        const translatedPersonality = await translateToChinese(rawPersonality);

        result[zodiac.value] = {
          element: translatedElement,
          personality: translatedPersonality
        };
      } catch (apiError) {
        console.error(`ËÆÄÂèñ ${zodiac.label} API Â§±Êïó`, apiError);
        result[zodiac.value] = null;
      }
    }

    setTraitsData(result);
    setIsTraitsLoading(false);
  }, []);

  useEffect(() => {
    fetchAllZodiacTraits();
  }, [fetchAllZodiacTraits]);

  // ---------------------- ‰∏âÂºµÂ°îÁæÖÁâå (Êñ∞Â¢ûÁøªË≠ØËàáÈåØË™§ËôïÁêÜ) ----------------------
  const handleDrawTarot = async () => {
    setIsTarotLoading(true);
    setTarotCards([]);

    try {
      const res = await fetch(`https://roxyapi.com/api/v1/data/astro/tarot/three-card-draw?token=${API_TOKEN}`);
      if (!res.ok) throw new Error(`Roxy API ÈåØË™§: ${res.status}`);
      const data = await res.json();

      // ‰ΩøÁî® map(async...) ËôïÁêÜÊØèÂºµÂç°ÁâáÁöÑÁøªË≠Ø
      const cardsPromises = data.map(async (card) => {
        let rawName = card.name;
        let rawMeaning = card.is_reversed ? (card.reversed_meaning || card.meaning) : card.meaning;

        // ÂòóË©¶ÁøªË≠ØÂêçÁ®±ÂíåËß£ÈáãÔºåËã•Â§±ÊïóÂâáËøîÂõûÂéüÂßãÊñáÊú¨
        const translatedName = await translateToChinese(rawName);
        const translatedMeaning = await translateToChinese(rawMeaning);

        return {
          name: translatedName,
          meaning: translatedMeaning,
          imageUrl: card.image,
          isReversed: card.is_reversed
        };
      });

      // Á≠âÂæÖÊâÄÊúâÂç°ÁâáËôïÁêÜÂÆåÊàê
      const cardResults = await Promise.all(cardsPromises);
      setTarotCards(cardResults);

    } catch (err) {
      console.error('Â°îÁæÖÁâå‰∏ªË¶Å API ËÆÄÂèñÂ§±Êïó:', err);
      alert('‚ùå ÊäΩÁâåÂ§±ÊïóÔºåË´ãÊ™¢Êü•Á∂≤Ë∑ØÈÄ£Á∑öÊàñÁ®çÂæåÂÜçË©¶„ÄÇ');
      setTarotCards([]);
    }
    setIsTarotLoading(false);
  };

  // ---------------------- NASA APOD ----------------------
  const fetchNasaApod = useCallback(async () => {
    setIsNasaLoading(true);
    setNasaError(null);

    try {
      const res = await fetch(`https://api.nasa.gov/planetary/apod?api_key=${NASA_API_KEY}`);
      if (!res.ok) throw new Error(`NASA API ÈåØË™§: ${res.status}`);
      const data = await res.json();

      // ÁøªË≠Ø explanationÔºàÂ¶ÇÊûúÊúâÔºâ
      let explanation = data.explanation || "";
      let translatedExplanation = explanation;
      try {
        translatedExplanation = await translateToChinese(explanation);
      } catch (e) {
        // Ëã•ÁøªË≠ØÂ§±ÊïóÂ∞±‰ΩøÁî®ÂéüÊñáÔºà‰ΩÜ‰øùË≠âÁπÅÈ´îÔºâ
        translatedExplanation = converter(explanation || "");
      }

      const normalized = {
        title: data.title || "",
        date: data.date || "",
        url: data.url || "",
        media_type: data.media_type || "image",
        explanation: translatedExplanation
      };

      setNasaData(normalized);
    } catch (err) {
      console.error("NASA APOD ËÆÄÂèñÂ§±ÊïóÔºö", err);
      setNasaError('ÁÑ°Ê≥ïËÆÄÂèñ NASA Ë≥áÊñôÔºåË´ãÁ®çÂæåÂÜçË©¶„ÄÇ');
      setNasaData(null);
    } finally {
      setIsNasaLoading(false);
    }
  }, []);

  // Âè™ÊúâÁï∂‰ΩøÁî®ËÄÖÂàáÂà∞ nasa tab ÊâçÂéªÊäìË≥áÊñôÔºàÈÅøÂÖçÂú®‰∏ÄÈñãÂßãÂ∞±È°çÂ§ñÂëºÂè´Ôºâ
  useEffect(() => {
    if (activeTab === 'nasa' && !nasaData && !isNasaLoading) {
      fetchNasaApod();
    }
  }, [activeTab, fetchNasaApod, nasaData, isNasaLoading]);

  // ---------------------- UI Ê∏≤Êüì ----------------------
  const renderConstellationSection = () => (
    <section className="content-section active">
      <h2>‰ªäÊó•ÊòüÂ∫ßÈÅãÂã¢ ‚ú®</h2>
      <div className="zodiac-selector">
        <select value={selectedZodiac} onChange={e => setSelectedZodiac(e.target.value)}>
          {ZODIAC_OPTIONS.map(z => <option key={z.value} value={z.value}>{z.label}</option>)}
        </select>
        <select value={selectedTime} onChange={e => setSelectedTime(e.target.value)}>
          {TIME_OPTIONS.map(t => <option key={t.value} value={t.value}>{t.label}</option>)}
        </select>
        <button onClick={handleShowFortune} disabled={isLoading}>
          {isLoading ? 'ÈÅãÁÆó‰∏≠...' : 'Êü•ÁúãÈÅãÂã¢'}
        </button>
      </div>
      <div className="result-box">
        <h3>{fortuneResult.title}</h3>
        <p>{fortuneResult.content}</p>
      </div>
    </section>
  );

  const renderInfoSection = () => (
    <section className="content-section">
      <h2>üìñ ÂçÅ‰∫åÊòüÂ∫ßÁâπË≥™</h2>
      {isTraitsLoading && <p>ËºâÂÖ•‰∏≠...</p>}
      <div className="constellation-grid">
        {ZODIAC_OPTIONS.map(z => {
          const info = traitsData[z.value];
          return (
            <div className="constellation-card" key={z.value}>
              <h3>{z.label}</h3>
              {!info && <p>ËºâÂÖ•‰∏≠ÊàñÂ∞öÁÑ°Ë≥áÊñô</p>}
              {info && (
                <>
                  <p><strong>‚ú® ÂÖÉÁ¥†Ôºö</strong> {info.element}</p>
                  <p><strong>üåü ÂÄãÊÄßÔºö</strong> {info.personality}</p>
                </>
              )}
            </div>
          );
        })}
      </div>
    </section>
  );

  const renderTarotSection = () => (
    <section className="content-section">
      <h2>üîÆ ‰∏âÂºµÂ°îÁæÖÁâåÂç†Âçú</h2>
      <button className="tarot-btn" onClick={handleDrawTarot} disabled={isTarotLoading}>
        {isTarotLoading ? 'ÊäΩÁâå‰∏≠...' : 'ÊäΩÂèñ‰∏âÂºµÂ°îÁæÖÁâå'}
      </button>
      <div className="tarot-area" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around', marginTop: '30px' }}>
        {tarotCards.map((card, idx) => (
          <div key={idx} style={{ width: '180px', textAlign: 'center', margin: '15px 10px' }}>
            <div
              style={{
                width: '100%',
                height: '280px',
                borderRadius: '15px',
                overflow: 'hidden',
                boxShadow: '0 10px 20px rgba(0,0,0,0.5)',
                marginBottom: '10px'
              }}
            >
              <img
                src={card.imageUrl}
                alt={card.name}
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'cover',
                  transform: card.isReversed ? 'rotate(180deg)' : 'none'
                }}
              />
            </div>
            <h3>{card.name}</h3>
            <p style={{ fontSize: '0.9rem', lineHeight: '1.3', color: '#ffd700' }}>{card.meaning}</p>
          </div>
        ))}
      </div>
    </section>
  );

  const renderNasaSection = () => (
    <section className="content-section">
      <h2>üöÄ NASA ‰ªäÊó•Â§©ÊñáÂúñÁâá</h2>

      {isNasaLoading && <p>ËºâÂÖ•‰∏≠‚Ä¶</p>}
      {!isNasaLoading && nasaError && <p style={{ color: 'salmon' }}>{nasaError}</p>}

      {!isNasaLoading && !nasaError && nasaData && (
        <div style={{ marginTop: 12 }}>
          {nasaData.media_type === 'image' ? (
            <img
              src={nasaData.url}
              alt={nasaData.title}
              style={{
                width: '100%',
                maxHeight: '420px',
                objectFit: 'cover',
                borderRadius: '12px',
                boxShadow: '0 10px 30px rgba(7,12,30,0.6)'
              }}
            />
          ) : (
            <iframe
              title="NASA Media"
              src={nasaData.url}
              width="100%"
              height="420"
              style={{ borderRadius: 12, border: 'none' }}
              allowFullScreen
            />
          )}

          <h3 style={{ marginTop: 12 }}>{nasaData.title}</h3>
          <p style={{ fontSize: '0.9rem', opacity: 0.95, whiteSpace: 'pre-wrap' }}>{nasaData.explanation}</p>
          {nasaData.date && <p style={{ fontSize: '0.8rem', opacity: 0.7, marginTop: 8 }}>Êó•ÊúüÔºö{nasaData.date}</p>}
        </div>
      )}

      {!isNasaLoading && !nasaError && !nasaData && <p>ÁõÆÂâçÊ≤íÊúâ NASA Ë≥áÊñô„ÄÇ</p>}
    </section>
  );

  return (
    <>
      <header>
