    try {
        const response = await fetch('https://libretranslate.com/translate', {
            method: 'POST',
            body: JSON.stringify({
                q: text,
                source: "en",
                target: "zh", // 中文
                format: "text",
            }),
            headers: { "Content-Type": "application/json" }
        });

        if (!response.ok) {
            // 如果 API 返回錯誤狀態碼，拋出錯誤，讓外層 catch 處理
            throw new Error(`LibreTranslate API Error: ${response.status}`);
        }

        const data = await response.json();
        const translatedText = data.translatedText;

        // 使用 OpenCC 將翻譯後的簡體中文轉換為繁體中文
        return converter(translatedText); 

    } catch (error) {
        console.warn("翻譯請求失敗，返回原文。", error);
        // 翻譯失敗時，返回原始文本
        return text; 
    }
};
