        const translatedName = await translateToChinese(rawName); 
        const translatedMeaning = await translateToChinese(rawMeaning); 

        return {
          name: translatedName,
          meaning: translatedMeaning,
          imageUrl: card.image,
          isReversed: card.is_reversed
        };
      });

      // 等待所有卡片處理完成
      const cardResults = await Promise.all(cardsPromises); 
      setTarotCards(cardResults);

