// Ê™îÊ°à: src/components/FortuneTeller.jsx
import React, { useState, useCallback, useEffect } from 'react';
import './FortuneTeller.css';
import { Converter } from 'opencc-js';

// Á∞°È´îËΩâÁπÅÈ´î (Á¢∫‰øù‰∏≠ÊñáÈÉΩÊòØÁπÅÈ´î)
const converter = Converter({ from: 'cn', to: 'tw' });

// ‚≠ê Roxy API Token
const API_TOKEN = '05239ac6-1087-4576-9fa9-d83860a4e722';

// -------------------- LibreTranslate ÁøªË≠ØËºîÂä©ÂáΩÂºè --------------------
const translateToChinese = async (text) => {
  if (!text || typeof text !== 'string' || text.trim() === '') return text;
  try {
    const response = await fetch('https://libretranslate.com/translate', {
      method: 'POST',
      body: JSON.stringify({
        q: text,
        source: 'en',
        target: 'zh',
        format: 'text',
      }),
      headers: { 'Content-Type': 'application/json' },
    });
    if (!response.ok) throw new Error(`LibreTranslate API Error: ${response.status}`);
    const data = await response.json();
    return converter(data.translatedText);
  } catch (error) {
    console.warn('ÁøªË≠ØÂ§±ÊïóÔºåËøîÂõûÂéüÊñá', error);
    return text;
  }
};

// -------------------- ÊòüÂ∫ß & ÊôÇÈñìÈÅ∏È†Ö --------------------
const ZODIAC_OPTIONS = [
  { value: 'aries', label: '‚ôà Áâ°ÁæäÂ∫ß' },
  { value: 'taurus', label: '‚ôâ ÈáëÁâõÂ∫ß' },
  { value: 'gemini', label: '‚ôä ÈõôÂ≠êÂ∫ß' },
  { value: 'cancer', label: '‚ôã Â∑®ËüπÂ∫ß' },
  { value: 'leo', label: '‚ôå ÁçÖÂ≠êÂ∫ß' },
  { value: 'virgo', label: '‚ôç ËôïÂ•≥Â∫ß' },
  { value: 'libra', label: '‚ôé Â§©Áß§Â∫ß' },
  { value: 'scorpio', label: '‚ôè Â§©Ë†çÂ∫ß' },
  { value: 'sagittarius', label: '‚ôê Â∞ÑÊâãÂ∫ß' },
  { value: 'capricorn', label: '‚ôë Êë©ÁæØÂ∫ß' },
  { value: 'aquarius', label: '‚ôí Ê∞¥Áì∂Â∫ß' },
  { value: 'pisces', label: '‚ôì ÈõôÈ≠öÂ∫ß' },
];

const TIME_OPTIONS = [
  { value: 'today', label: '‰ªäÊó•' },
  { value: 'week', label: 'Êú¨ÈÄ±' },
  { value: 'month', label: 'Êú¨Êúà' },
];

// -------------------- ‰∏ªÂÖÉ‰ª∂ --------------------
const FortuneTeller = () => {
  const [activeTab, setActiveTab] = useState('constellation');

  // ÊòüÂ∫ßÈÅãÂã¢
  const [selectedZodiac, setSelectedZodiac] = useState(ZODIAC_OPTIONS[0].value);
  const [selectedTime, setSelectedTime] = useState(TIME_OPTIONS[0].value);
  const [fortuneResult, setFortuneResult] = useState({
    title: 'Ë´ãÈÅ∏ÊìáÊÇ®ÁöÑÊòüÂ∫ß‰∏¶ÈªûÊìä„ÄåÊü•ÁúãÈÅãÂã¢„Äç‰ª•ÈñãÂßã„ÄÇ',
    content: '',
  });
  const [isLoading, setIsLoading] = useState(false);

  // ÊòüÂ∫ßÁâπË≥™
  const [traitsData, setTraitsData] = useState({});
  const [isTraitsLoading, setIsTraitsLoading] = useState(false);

  // ‰∏âÂºµÂ°îÁæÖÁâå
  const [tarotCards, setTarotCards] = useState([]);
  const [isTarotLoading, setIsTarotLoading] = useState(false);

  // NASA APOD
  const [nasaData, setNasaData] = useState({ title: '', explanation: '', url: '' });
  const [isNasaLoading, setIsNasaLoading] = useState(true);

  // -------------------- ÊòüÂ∫ßÈÅãÂã¢ --------------------
  const handleShowFortune = useCallback(async () => {
    setIsLoading(true);
    setFortuneResult({ title: 'üîÆ Ê≠£Âú®ËÅØÁπ´ÂëΩÈÅã‰πãËº™...', content: '' });
    const apiUrl = `https://v2.xxapi.cn/api/horoscope?type=${selectedZodiac}&time=${selectedTime}`;
    try {
      const response = await fetch(apiUrl);
      if (!response.ok) throw new Error(`API ÈåØË™§: ${response.status}`);
      const data = await response.json();
      let fortuneText = data.data?.fortunetext?.all || 'ÈÅãÂã¢Êö´ÁÑ°Ë≥áÊñô„ÄÇ';
      fortuneText = fortuneText.replace(/Êòü\d+Â∫ß\d+Â±ã[^\u4e00-\u9fa5]*/g, '').trim();
      fortuneText = converter(fortuneText);
      setFortuneResult({
        title: `${ZODIAC_OPTIONS.find(z => z.value === selectedZodiac)?.label} ${TIME_OPTIONS.find(t => t.value === selectedTime)?.label}ÈÅãÂã¢Ôºö`,
        content: fortuneText,
      });
    } catch (err) {
      console.error('ÊòüÂ∫ßÈÅãÂã¢ API ËÆÄÂèñÂ§±Êïó:', err);
      setFortuneResult({ title: '‚ùå ÈÅãÂã¢ËÆÄÂèñÂ§±Êïó', content: 'Ë´ãÁ®çÂæåÂÜçË©¶„ÄÇ' });
    }
    setIsLoading(false);
  }, [selectedZodiac, selectedTime]);

  // -------------------- ÊòüÂ∫ßÁâπË≥™ --------------------
  const fetchAllZodiacTraits = useCallback(async () => {
    setIsTraitsLoading(true);
    const result = {};
    for (const zodiac of ZODIAC_OPTIONS) {
      try {
        const url = `https://roxyapi.com/api/v1/data/astro/astrology/zodiac/${zodiac.value}?token=${API_TOKEN}`;
        const res = await fetch(url);
        if (!res.ok) throw new Error(`Roxy API ÈåØË™§: ${res.status}`);
        const data = await res.json();
        const translatedElement = await translateToChinese(data.element || '');
        const translatedPersonality = await translateToChinese(data.personality || '');
        result[zodiac.value] = { element: translatedElement, personality: translatedPersonality };
      } catch (err) {
        console.error(`ËÆÄÂèñ ${zodiac.label} API Â§±Êïó`, err);
        result[zodiac.value] = null;
      }
    }
    setTraitsData(result);
    setIsTraitsLoading(false);
  }, []);

  useEffect(() => {
    fetchAllZodiacTraits();
  }, [fetchAllZodiacTraits]);

  // -------------------- ‰∏âÂºµÂ°îÁæÖÁâå --------------------
  const handleDrawTarot = async () => {
    setIsTarotLoading(true);
    setTarotCards([]);
    try {
      const res = await fetch(`https://roxyapi.com/api/v1/data/astro/tarot/three-card-draw?token=${API_TOKEN}`);
      if (!res.ok) throw new Error(`Roxy API ÈåØË™§: ${res.status}`);
      const data = await res.json();
      const cardsPromises = data.map(async card => {
        const translatedName = await translateToChinese(card.name);
        const meaning = card.is_reversed ? (card.reversed_meaning || card.meaning) : card.meaning;
        const translatedMeaning = await translateToChinese(meaning);
        return {
          name: translatedName,
          meaning: translatedMeaning,
          imageUrl: card.image,
          isReversed: card.is_reversed,
        };
      });
      const cardResults = await Promise.all(cardsPromises);
      setTarotCards(cardResults);
    } catch (err) {
      console.error('Â°îÁæÖÁâå‰∏ªË¶Å API ËÆÄÂèñÂ§±Êïó:', err);
      alert('‚ùå ÊäΩÁâåÂ§±ÊïóÔºåË´ãÁ®çÂæåÂÜçË©¶„ÄÇ');
      setTarotCards([]);
    }
    setIsTarotLoading(false);
  };

  // -------------------- NASA APOD --------------------
  const fetchNasaApod = async () => {
    setIsNasaLoading(true);
    try {
      const res = await fetch(`https://api.nasa.gov/planetary/apod?api_key=DEMO_KEY`);
      const data = await res.json();
      const translatedExplanation = await translateToChinese(data.explanation || '');
      setNasaData({ title: data.title, explanation: translatedExplanation, url: data.url });
    } catch (err) {
      console.error('NASA APOD ËÆÄÂèñÂ§±Êïó:', err);
      setNasaData({ title: '‚ùå ËÆÄÂèñÂ§±Êïó', explanation: 'Ë´ãÁ®çÂæåÂÜçË©¶', url: '' });
    }
    setIsNasaLoading(false);
  };

  useEffect(() => {
    fetchNasaApod();
  }, []);

  // -------------------- UI Ê∏≤Êüì --------------------
  const renderConstellationSection = () => (
    <section className="content-section active">
      <h2>‰ªäÊó•ÊòüÂ∫ßÈÅãÂã¢ ‚ú®</h2>
      <div className="zodiac-selector">
        <select value={selectedZodiac} onChange={e => setSelectedZodiac(e.target.value)}>
          {ZODIAC_OPTIONS.map(z => <option key={z.value} value={z.value}>{z.label}</option>)}
        </select>
        <select value={selectedTime} onChange={e => setSelectedTime(e.target.value)}>
          {TIME_OPTIONS.map(t => <option key={t.value} value={t.value}>{t.label}</option>)}
        </select>
        <button onClick={handleShowFortune} disabled={isLoading}>
          {isLoading ? 'ÈÅãÁÆó‰∏≠...' : 'Êü•ÁúãÈÅãÂã¢'}
        </button>
      </div>
      <div className="result-box">
        <h3>{fortuneResult.title}</h3>
        <p>{fortuneResult.content}</p>
      </div>
    </section>
  );

  const renderInfoSection = () => (
    <section className="content-section">
      <h2>üìñ ÂçÅ‰∫åÊòüÂ∫ßÁâπË≥™</h2>
      {isTraitsLoading && <p>ËºâÂÖ•‰∏≠...</p>}
      <div className="constellation-grid">
        {ZODIAC_OPTIONS.map(z => {
          const info = traitsData[z.value];
          return (
            <div className="constellation-card" key={z.value}>
              <h3>{z.label}</h3>
              {!info && <p>ËºâÂÖ•‰∏≠ÊàñÂ∞öÁÑ°Ë≥áÊñô</p>}
              {info && (
                <>
                  <p><strong>‚ú® ÂÖÉÁ¥†Ôºö</strong> {info.element}</p>
                  <p><strong>üåü ÂÄãÊÄßÔºö</strong> {info.personality}</p>
                </>
              )}
            </div>
          );
        })}
      </div>
    </section>
  );

  const renderTarotSection = () => (
    <section className="content-section">
      <h2>üîÆ ‰∏âÂºµÂ°îÁæÖÁâåÂç†Âçú</h2>
      <button className="tarot-btn" onClick={handleDrawTarot} disabled={isTarotLoading}>
        {isTarotLoading ? 'ÊäΩÁâå‰∏≠...' : 'ÊäΩÂèñ‰∏âÂºµÂ°îÁæÖÁâå'}
      </button>
      <div className="tarot-area" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around', marginTop: '30px' }}>
        {tarotCards.map((card, idx) => (
          <div key={idx} style={{ width: '180px', textAlign: 'center', margin: '15px 10px' }}>
            <div
              style={{
                width: '100%',
                height: '280px',
                borderRadius: '15px',
                overflow: 'hidden',
                boxShadow: '0 10px 20px rgba(0,0,0,0.5)',
                marginBottom: '10px'
              }}
            >
              <img
                src={card.imageUrl}
                alt={card.name}
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'cover',
                  transform: card.isReversed ? 'rotate(180deg)' : 'none'
                }}
              />
            </div>
            <h3>{card.name}</h3>
            <p style={{ fontSize: '0.9rem', lineHeight: '1.3', color: '#ffd700' }}>{card.meaning}</p>
          </div>
        ))}
      </div>
    </section>
  );

  const renderNasaSection = () => (
    <section className="content-section">
      <h2>üöÄ NASA Â§©ÊñáÊØèÊó•ÂúñÁâá (APOD)</h2>
      {isNasaLoading ? <p>ËºâÂÖ•‰∏≠...</p> : (
        <div style={{ textAlign: 'center' }}>
          <h3>{nasaData.title}</h3>
          {nasaData.url && <img src={nasaData.url} alt={nasaData.title} style={{ maxWidth: '100%', borderRadius: '15px' }} />}
          <p style={{ marginTop: '15px', lineHeight: '1.5' }}>{nasaData.explanation}</p>
        </div>
      )}
    </section>
  );

  return (
    <>
      <header>
        <h1>üåü ÂëΩÈÅã‰πãËº™ üåü</h1>
        <nav>
          <button className={`nav-btn ${activeTab === 'constellation' ? 'active' : ''}`} onClick={() => setActiveTab('constellation')}>ÊòüÂ∫ßÈÅãÂã¢</button>
          <button className={`nav-btn ${activeTab === 'tarot' ? 'active' : ''}`} onClick={() => setActiveTab('tarot')}>Â°îÁæÖÂç†Âçú</button>
          <button className={`nav-btn ${activeTab === 'info' ? 'active' : ''}`} onClick={() => setActiveTab('info')}>ÊòüÂ∫ß‰ªãÁ¥π</button>
          <button className={`nav-btn ${activeTab === 'nasa' ? 'active' : ''}`} onClick={() => setActiveTab('nasa')}>NASA APOD</button>
        </nav>
      </header>

      <main>
        {activeTab === 'constellation' && renderConstellationSection()}
        {activeTab === 'tarot' && renderTarotSection()}
        {activeTab === 'info' && renderInfoSection()}
        {activeTab === 'nasa' && renderNasaSection()}
      </main>

      <footer>
        <p>&copy; 2025 ÂëΩÈÅã‰πãËº™È≠îÊ≥ïÂ±ã„ÄÇÊâÄÊúâÂÖßÂÆπÂÉÖ‰æõÂ®õÊ®ÇÂèÉËÄÉ„ÄÇ</p>
      </footer>
    </>
  );
};

export default FortuneTeller;
