// Ê™îÊ°à: src/components/FortuneTeller.jsx
import React, { useState, useCallback } from 'react';
import './FortuneTeller.css';
import { Converter } from 'opencc-js';

// Á∞°È´îËΩâÁπÅÈ´î
const converter = Converter({ from: 'cn', to: 'tw' });

const ZODIAC_OPTIONS = [
    { value: "aries", label: "‚ôà Áâ°ÁæäÂ∫ß" },
    { value: "taurus", label: "‚ôâ ÈáëÁâõÂ∫ß" },
    { value: "gemini", label: "‚ôä ÈõôÂ≠êÂ∫ß" },
    { value: "cancer", label: "‚ôã Â∑®ËüπÂ∫ß" },
    { value: "leo", label: "‚ôå ÁçÖÂ≠êÂ∫ß" },
    { value: "virgo", label: "‚ôç ËôïÂ•≥Â∫ß" },
    { value: "libra", label: "‚ôé Â§©Áß§Â∫ß" },
    { value: "scorpio", label: "‚ôè Â§©Ë†çÂ∫ß" },
    { value: "sagittarius", label: "‚ôê Â∞ÑÊâãÂ∫ß" },
    { value: "capricorn", label: "‚ôë Êë©ÁæØÂ∫ß" },
    { value: "aquarius", label: "‚ôí Ê∞¥Áì∂Â∫ß" },
    { value: "pisces", label: "‚ôì ÈõôÈ≠öÂ∫ß" },
];

const TIME_OPTIONS = [
    { value: "today", label: "‰ªäÊó•" },
    { value: "week", label: "Êú¨ÈÄ±" },
    { value: "month", label: "Êú¨Êúà" },
];

const ROXY_API_KEY = '8e66fa8b-7990-47ac-8f03-944cef9a180d'; // <-- Ë´ãÂ°´ÂÖ•‰Ω†ÁöÑ key

const FortuneTeller = () => {
    const [activeTab, setActiveTab] = useState('constellation');
    const [selectedZodiac, setSelectedZodiac] = useState(ZODIAC_OPTIONS[0].value);
    const [selectedTime, setSelectedTime] = useState(TIME_OPTIONS[0].value);
    const [fortuneResult, setFortuneResult] = useState({ title: 'Ë´ãÈÅ∏ÊìáÊÇ®ÁöÑÊòüÂ∫ß‰∏¶ÈªûÊìä„ÄåÊü•ÁúãÈÅãÂã¢„Äç‰ª•ÈñãÂßã„ÄÇ', content: '' });
    const [isLoading, setIsLoading] = useState(false);

    const [tarotCardRevealed, setTarotCardRevealed] = useState(false);
    const [tarotResult, setTarotResult] = useState({ title: 'Â°îÁæÖÁâåÁµêÊûúÂ∞áÂú®Ê≠§ËôïÊè≠Á§∫„ÄÇ', meaning: '', cardName: '', imageUrl: '' });

    const [traitsData, setTraitsData] = useState({});
    const [isTraitsLoading, setIsTraitsLoading] = useState(false);

    // ÊòüÂ∫ßÈÅãÂã¢
    const handleShowFortune = useCallback(async () => {
        setIsLoading(true);
        setFortuneResult({ title: 'üîÆ Ê≠£Âú®ËÅØÁπ´ÂëΩÈÅã‰πãËº™ÔºåË´ãÁ®çÂÄô...', content: '' });

        const apiUrl = `https://v2.xxapi.cn/api/horoscope?type=${selectedZodiac}&time=${selectedTime}`;

        try {
            const response = await fetch(apiUrl);
            if (!response.ok) throw new Error(`API ÈåØË™§: ${response.status}`);

            const data = await response.json();
            const selectedLabel = ZODIAC_OPTIONS.find(opt => opt.value === selectedZodiac)?.label || 'ÊòüÂ∫ß';
            let fortuneText = data.data?.fortunetext?.all || 'ÈÅãÂã¢Êö´ÁÑ°Ë≥áÊñô„ÄÇ';

            // ÂéªÊéâ„ÄåÊòü6Â∫ß6Â±ã„ÄçÁ≠âÈõúË®ä
            fortuneText = fortuneText.replace(/Êòü\d+Â∫ß\d+Â±ã[^\u4e00-\u9fa5]*/g, '').trim();

            // Á∞°È´îËΩâÁπÅÈ´î
            fortuneText = converter(fortuneText);

            setFortuneResult({
                title: `${selectedLabel} ${TIME_OPTIONS.find(t => t.value === selectedTime)?.label}ÈÅãÂã¢Ôºö`,
                content: fortuneText
            });

        } catch (error) {
            console.error(error);
            setFortuneResult({
                title: '‚ùå ÈÅãÂã¢ËÆÄÂèñÂ§±Êïó„ÄÇ',
                content: 'Ë´ãÁ¢∫Ë™çÁ∂≤Ë∑ØÊàñ API ÊòØÂê¶ÂèØÁî®„ÄÇ'
            });
        } finally {
            setIsLoading(false);
        }
    }, [selectedZodiac, selectedTime]);

    // Â°îÁæÖÁâå
    const resetTarot = () => {
        setTarotCardRevealed(false);
        setTarotResult({ title: 'Â°îÁæÖÁâåÁµêÊûúÂ∞áÂú®Ê≠§ËôïÊè≠Á§∫„ÄÇ', meaning: '', cardName: '', imageUrl: '' });
    };

    const handleDrawTarot = async () => {
        if (tarotCardRevealed) {
            resetTarot();
            return;
        }

        try {
            const response = await fetch('https://tarotapi.dev/api/v1/cards/random?n=1');
            if (!response.ok) throw new Error(`Â°îÁæÖ API ÈåØË™§: ${response.status}`);
            const data = await response.json();

            const card = data.cards[0];
            const meaning = card.meaning_up || card.meaning_rev || 'ÁÑ°Ëß£Èáã';

            setTarotCardRevealed(true);
            setTarotResult({
                title: `üîÆ ÊÅ≠ÂñúÊäΩÂà∞ „Äê${card.name}„Äë`,
                meaning: meaning,
                cardName: card.name,
                imageUrl: card.image
            });
        } catch (error) {
            console.error(error);
            setTarotResult({
                title: '‚ùå Â°îÁæÖÁâåÊäΩÂèñÂ§±Êïó',
                meaning: 'Ë´ãÊ™¢Êü•Á∂≤Ë∑ØÊàñ API ÊòØÂê¶ÂèØÁî®',
                cardName: '',
                imageUrl: ''
            });
        }
    };

    // ÊòüÂ∫ßÁâπË≥™ - ‰ΩøÁî® RoxyAPI
    const fetchAllZodiacTraits = useCallback(async () => {
        setIsTraitsLoading(true);
        try {
            const results = {};
            for (const zodiac of ZODIAC_OPTIONS) {
                const res = await fetch(`https://api.roxyapi.com/astrology/zodiac/${zodiac.value}?api_key=${ROXY_API_KEY}`);
                if (!res.ok) throw new Error(`RoxyAPI ÊòüÂ∫ßË≥áÊñôÈåØË™§: ${res.status}`);
                const data = await res.json();

                results[zodiac.value] = {
                    element: converter(data.element || ''),
                    traits: data.traits ? data.traits.map(t => converter(t)) : [],
                    keywords: data.keywords ? data.keywords.map(k => converter(k)) : [],
                    description: converter(data.description || '')
                };
            }
            setTraitsData(results);
        } catch (err) {
            console.error("Traits API Error:", err);
        } finally {
            setIsTraitsLoading(false);
        }
    }, []);

    // ‰ª•‰∏ã JSX ÂÖ∂‰ªñÈÉ®ÂàÜ‰∏çÁî®Êîπ
    // Âè™ÈúÄÁ¢∫‰øù renderInfoSection ‰ΩøÁî® description
    const renderInfoSection = () => (
        <section className="content-section">
            <h2>üìñ ÂçÅ‰∫åÊòüÂ∫ßÁâπË≥™</h2>
            {isTraitsLoading && <p>Ë≥áÊñôËºâÂÖ•‰∏≠...</p>}
            <div className="constellation-grid">
                {ZODIAC_OPTIONS.map(opt => {
                    const data = traitsData[opt.value];
                    return (
                        <div className="constellation-card" key={opt.value}>
                            <h3>{opt.label}</h3>
                            {!data && <p>Â∞öÁÑ°Ë≥áÊñô</p>}
