import React, { useState, useCallback, useEffect } from "react";
import "./FortuneTeller.css";
import { Converter } from "opencc-js";

// Á∞°È´îËΩâÁπÅÈ´î
const converter = Converter({ from: "cn", to: "tw" });

// API Keys
const API_TOKEN = "05239ac6-1087-4576-9fa9-d83860a4e722";
const NASA_API_KEY = "t9Z5pJEV980lItvNoFknpSKBeqtLMnaPAP7akzg4";

// ----------------------------------------------------
// ÁøªË≠ØÔºàËµ∞Êú¨Âú∞ serverÔºâ
// ----------------------------------------------------
const translateToChinese = async (text) => {
  if (!text || typeof text !== "string") return text;

  try {
    const res = await fetch("http://localhost:3001/api/translate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text }),
    });

    const data = await res.json();
    return converter(data.text || text);
  } catch {
    return text;
  }
};

// ----------------------------------------------------
// ÊòüÂ∫ß / ÊôÇÈñì
// ----------------------------------------------------
const ZODIAC_OPTIONS = [
  { value: "aries", label: "‚ôà Áâ°ÁæäÂ∫ß" },
  { value: "taurus", label: "‚ôâ ÈáëÁâõÂ∫ß" },
  { value: "gemini", label: "‚ôä ÈõôÂ≠êÂ∫ß" },
  { value: "cancer", label: "‚ôã Â∑®ËüπÂ∫ß" },
  { value: "leo", label: "‚ôå ÁçÖÂ≠êÂ∫ß" },
  { value: "virgo", label: "‚ôç ËôïÂ•≥Â∫ß" },
  { value: "libra", label: "‚ôé Â§©Áß§Â∫ß" },
  { value: "scorpio", label: "‚ôè Â§©Ë†çÂ∫ß" },
  { value: "sagittarius", label: "‚ôê Â∞ÑÊâãÂ∫ß" },
  { value: "capricorn", label: "‚ôë Êë©ÁæØÂ∫ß" },
  { value: "aquarius", label: "‚ôí Ê∞¥Áì∂Â∫ß" },
  { value: "pisces", label: "‚ôì ÈõôÈ≠öÂ∫ß" },
];

const TIME_OPTIONS = [
  { value: "today", label: "‰ªäÊó•" },
  { value: "week", label: "Êú¨ÈÄ±" },
  { value: "month", label: "Êú¨Êúà" },
];

// ----------------------------------------------------
const FortuneTeller = () => {
  const [activeTab, setActiveTab] = useState("constellation");

  // ÊòüÂ∫ßÈÅãÂã¢
  const [selectedZodiac, setSelectedZodiac] = useState("aries");
  const [selectedTime, setSelectedTime] = useState("today");
  const [fortuneResult, setFortuneResult] = useState({ title: "", content: "" });
  const [isLoading, setIsLoading] = useState(false);

  // ÊòüÂ∫ßÁâπË≥™
  const [traitsData, setTraitsData] = useState({});
  const [isTraitsLoading, setIsTraitsLoading] = useState(false);

  // Â°îÁæÖ
  const [tarotCards, setTarotCards] = useState([]);
  const [isTarotLoading, setIsTarotLoading] = useState(false);

  // NASA
  const [nasaData, setNasaData] = useState(null);
  const [isNasaLoading, setIsNasaLoading] = useState(false);
  const [nasaError, setNasaError] = useState(null);

  // ---------------- ÊòüÂ∫ßÈÅãÂã¢ ----------------
  const handleShowFortune = async () => {
    setIsLoading(true);
    try {
      const res = await fetch(
        `https://v2.xxapi.cn/api/horoscope?type=${selectedZodiac}&time=${selectedTime}`
      );
      const data = await res.json();

      const text = converter(data.data?.fortunetext?.all || "Êö´ÁÑ°Ë≥áÊñô");
      setFortuneResult({
        title: `${ZODIAC_OPTIONS.find(z => z.value === selectedZodiac)?.label} ${TIME_OPTIONS.find(t => t.value === selectedTime)?.label}`,
        content: text,
      });
    } catch {
      setFortuneResult({ title: "ÈåØË™§", content: "ËÆÄÂèñÂ§±Êïó" });
    }
    setIsLoading(false);
  };

  // ---------------- ÊòüÂ∫ßÁâπË≥™ ----------------
  const fetchAllZodiacTraits = useCallback(async () => {
    setIsTraitsLoading(true);
    const result = {};

    for (const z of ZODIAC_OPTIONS) {
      try {
        const res = await fetch(
          `https://roxyapi.com/api/v1/data/astro/astrology/zodiac/${z.value}?token=${API_TOKEN}`
        );
        const data = await res.json();

        result[z.value] = {
          element: await translateToChinese(data.element),
          personality: await translateToChinese(data.personality),
        };
      } catch {
        result[z.value] = null;
      }
    }

    setTraitsData(result);
    setIsTraitsLoading(false);
  }, []);

  useEffect(() => {
    fetchAllZodiacTraits();
  }, [fetchAllZodiacTraits]);

  // ---------------- Â°îÁæÖ ----------------
  const handleDrawTarot = async () => {
    setIsTarotLoading(true);
    setTarotCards([]);

    try {
      const res = await fetch(
        `https://roxyapi.com/api/v1/data/astro/tarot/three-card-draw?token=${API_TOKEN}`
      );
      const data = await res.json();

      const cards = await Promise.all(
        data.map(async (c) => ({
          name: await translateToChinese(c.name),
          meaning: await translateToChinese(
            c.is_reversed ? c.reversed_meaning || c.meaning : c.meaning
          ),
          image: c.image,
          reversed: c.is_reversed,
        }))
      );

      setTarotCards(cards);
    } catch {
      alert("ÊäΩÁâåÂ§±Êïó");
    }
    setIsTarotLoading(false);
  };

  // ---------------- NASA ----------------
  const fetchNasaApod = useCallback(async () => {
    setIsNasaLoading(true);
    try {
      const res = await fetch(
        `https://api.nasa.gov/planetary/apod?api_key=${NASA_API_KEY}`
      );
      const data = await res.json();
      setNasaData({
        ...data,
        explanation: await translateToChinese(data.explanation),
      });
    } catch {
      setNasaError("NASA ËÆÄÂèñÂ§±Êïó");
    }
    setIsNasaLoading(false);
  }, []);

  useEffect(() => {
    if (activeTab === "nasa" && !nasaData) fetchNasaApod();
  }, [activeTab, fetchNasaApod, nasaData]);

  // ---------------- UI ----------------
  return (
    <>
      <header>
        <h1>üåü ÂëΩÈÅã‰πãËº™ üåü</h1>
        <nav>
          <button onClick={() => setActiveTab("constellation")}>ÊòüÂ∫ßÈÅãÂã¢</button>
          <button onClick={() => setActiveTab("tarot")}>Â°îÁæÖÂç†Âçú</button>
          <button onClick={() => setActiveTab("info")}>ÊòüÂ∫ß‰ªãÁ¥π</button>
          <button onClick={() => setActiveTab("nasa")}>NASA Â§™Á©∫</button>
        </nav>
      </header>

      <main>
        {activeTab === "constellation" && (
          <section>
            <select value={selectedZodiac} onChange={e => setSelectedZodiac(e.target.value)}>
              {ZODIAC_OPTIONS.map(z => (
                <option key={z.value} value={z.value}>{z.label}</option>
              ))}
            </select>

            <select value={selectedTime} onChange={e => setSelectedTime(e.target.value)}>
              {TIME_OPTIONS.map(t => (
                <option key={t.value} value={t.value}>{t.label}</option>
              ))}
            </select>

            <button onClick={handleShowFortune}>
              {isLoading ? "ËÆÄÂèñ‰∏≠..." : "Êü•ÁúãÈÅãÂã¢"}
            </button>

            <h3>{fortuneResult.title}</h3>
            <p>{fortuneResult.content}</p>
          </section>
        )}

        {activeTab === "info" && (
          <section className="constellation-grid">
            {ZODIAC_OPTIONS.map(z => (
              <div key={z.value} className="constellation-card">
                <h3>{z.label}</h3>
                <p>{traitsData[z.value]?.personality}</p>
              </div>
            ))}
          </section>
        )}

        {activeTab === "tarot" && (
